import React, { Component } from "react";
class User extends Component {
  state = {
    count: 12,
    txt: "胡椒粉的考虑时间",
    obj: { name: "张三", age: 12 },
  };
  setCount = () => {
    this.setState((prevCount) => {
      return {
        count: prevCount.count + 1,
        obj: { ...this.state.obj, name: "李四" },
      };
    });
  };
  divRef=React.createRef()
  render() {
    return (
      <div>
        <h3>{this.state.count}</h3>
        <h3>{this.state.txt}</h3>
        <h3>
          {this.state.obj.name}-{this.state.obj.age}
        </h3>
        <button onClick={this.setCount}>按钮</button>
        <ul>
          <li>姓名：{this.props.user.name}</li>
          <li>年龄：{this.props.user.age}</li>
        </ul>
      </div>
    );
  }
}

export default User;
