import React from "react";
import "./App.css";
import { useState } from "react";
const App = () => {
  const add = () => {
    setCount(count+1);
  };
  const less = () => {
    setCount(count-1);
  };
//   const state=useState(1);
//   let count=state[0];
//   let setCount=state[1];
  return (
    <div className="app">
      <h1>{count}</h1>
      <button onClick={less}>-</button>
      <button onClick={add}>+</button>
    </div>
  );
};
export default App;
