import React from "react";
import { useEffect, useState } from "react";
import { useReducer } from "react";
const App = () => {
  // const [count, setCount] = useState(0);
  const add = () => {
    setCount((prevState) => {
      return prevState + 1;
    });
  };
  const sub = () => {
    setCount((prevState) => {
      return prevState - 1;
    });
  };
  const [count,countD]=useReducer((state) => {
    return state+1;
  }, 0);
  return (
    <div
      style={{
        fontSize: 20,
        width: 200,
        height: 200,
        margin: "100px auto",
        textAlign: "center",
      }}
    >
      <button onClick={sub}>减少</button>
      {count}
      <button onClick={add}>增加</button>
    </div>
  );
};

export default App;
