import React from "react";
import { useReducer } from "react";
const App = () => {
  const [count, countDispatch] = useReducer((state, action) => {
    // if (action.type === "ADD") return state + 1;
    // else if (action.type === "SUB") return state - 1;
    // else return state;
    switch (action.type) {
      case "ADD":
        return state + 1;
        break;
    }
  }, 0);
  const add = () => {
    countDispatch({ type: "ADD" });
  };
  const sub = () => {
    countDispatch({ type: "SUB" });
  };
  return (
    <div
      style={{
        fontSize: 20,
        width: 200,
        height: 200,
        margin: "100px auto",
        textAlign: "center",
      }}
    >
      <button onClick={sub}>减少</button>
      {count}
      <button onClick={add}>增加</button>
    </div>
  );
};

export default App;
